import React, { useState } from 'react';

function TrackForm({ onTracked }) {
  const [url, setUrl] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    if (!url.startsWith('https://www.amazon')) {
      setError('Please enter a valid Amazon URL');
      return;
    }
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/products/track', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ url }),
      });
      const data = await response.json();
      if (response.ok) {
        onTracked(data);
        setUrl('');
      } else {
        setError(data.error || 'Failed to track product');
      }
    } catch (err) {
      setError('Network error');
    }
    setLoading(false);
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: 24 }}>
      <input
        type="text"
        placeholder="Enter Amazon Product URL"
        value={url}
        onChange={e => setUrl(e.target.value)}
        style={{ width: 400, padding: 8, fontSize: 16 }}
        required
        disabled={loading}
      />
      <button
        type="submit"
        style={{ marginLeft: 12, padding: '8px 20px', fontSize: 16 }}
        disabled={loading}
      >
        {loading ? 'Tracking...' : 'Track'}
      </button>
      {error && <div style={{ color: 'red', marginTop: 8 }}>{error}</div>}
    </form>
  );
}

export default TrackForm;
