import React from 'react';
import { LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid, ResponsiveContainer } from 'recharts';

function PriceGraph({ history }) {
  if (!history || history.length === 0) {
    return <div style={{ color: '#aaa', marginBottom: 24 }}>[No price history yet]</div>;
  }
  const data = history.map(item => ({
    price: item.price,
    date: new Date(item.timestamp || item.date).toLocaleString(),
  }));

  return (
    <div style={{ width: '100%', height: 320, marginBottom: 24 }}>
      <ResponsiveContainer>
        <LineChart data={data}>
          <XAxis dataKey="date" />
          <YAxis />
          <Tooltip />
          <CartesianGrid stroke="#eee" />
          <Line type="monotone" dataKey="price" stroke="#2196f3" />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
}

export default PriceGraph;
