import React, { useState } from 'react';

function TrackForm({ onTracked }) {
  const [url, setUrl] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!url.startsWith('https://www.amazon')) {
      alert('Please enter a valid Amazon URL');
      return;
    }
    // Call backend API to start tracking
    const response = await fetch('http://localhost:5000/api/products/track', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ url }),
    });
    const data = await response.json();
    onTracked(data); // Pass product data up
    setUrl('');
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: 24 }}>
      <input
        type="text"
        placeholder="Enter Amazon Product URL"
        value={url}
        onChange={e => setUrl(e.target.value)}
        style={{ width: 400, padding: 8, fontSize: 16 }}
        required
      />
      <button type="submit" style={{ marginLeft: 12, padding: '8px 20px', fontSize: 16 }}>
        Track
      </button>
    </form>
  );
}

export default TrackForm;
