import React, { useState } from 'react';

function TrackForm({ onTracked }) {
  const [url, setUrl] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!url) return;

    try {
      const res = await fetch('http://localhost:5000/api/products/track', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ url })
      });
      const data = await res.json();

      if (res.ok) {
        onTracked(data); // pass full product object to App.js
      } else {
        alert(data.error);
      }
    } catch (err) {
      console.error(err);
      alert('Failed to track product');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: 20 }}>
      <input
        value={url}
        onChange={(e) => setUrl(e.target.value)}
        placeholder="Enter Amazon product URL"
        style={{ width: '80%', padding: 8, marginRight: 10 }}
      />
      <button type="submit" style={{ padding: 8 }}>Track</button>
    </form>
  );
}

export default TrackForm;
