import React from 'react';

function ProductPreview({ product }) {
  if (!product) return <div style={{ marginBottom: 24 }}>No product tracked yet.</div>;
  return (
    <div style={{ marginBottom: 24, display: 'flex', alignItems: 'center' }}>
      <img
        src={product.image || 'https://via.placeholder.com/80x80?text=No+Image'}
        alt={product.name}
        style={{ width: 80, height: 80, objectFit: 'contain', marginRight: 16, border: '1px solid #ddd', background: '#fafafa' }}
      />
      <div>
        <div style={{ fontWeight: 'bold' }}>{product.name}</div>
        <div>Current Price: â‚¹{product.currentPrice ?? 'N/A'}</div>
        <a href={product.url} target="_blank" rel="noopener noreferrer">View on Amazon</a>
      </div>
    </div>
  );
}

export default ProductPreview;
