import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';

function App() {
  const [url, setUrl] = useState('');
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleTrack = async () => {
    setLoading(true);
    setError(null);
    setProduct(null);
    try {
      const response = await axios.post('http://localhost:5000/api/products/track', { url });
      setProduct(response.data);
    } catch (err) {
      setError(err.response?.data?.error || 'Error tracking product');
    } finally {
      setLoading(false);
    }
  };

  const chartData = product && {
    labels: product.priceHistory.map(entry => new Date(entry.timestamp).toLocaleString()),
    datasets: [
      {
        label: 'Price History',
        data: product.priceHistory.map(entry => entry.price),
        fill: false,
        borderColor: '#007bff',
        tension: 0.3,
        pointRadius: 3
      }
    ]
  };

  return (
    <div style={{ padding: 30, fontFamily: 'Arial, sans-serif' }}>
      <h2>Amazon Price Tracker</h2>
      <div style={{ marginBottom: 20 }}>
        <input
          type="text"
          value={url}
          onChange={e => setUrl(e.target.value)}
          placeholder="Paste Amazon product URL"
          style={{ width: '400px', padding: '8px', fontSize: '14px', marginRight: '10px' }}
        />
        <button
          onClick={handleTrack}
          disabled={loading}
          style={{ padding: '8px 16px', fontSize: '14px', cursor: loading ? 'not-allowed' : 'pointer' }}
        >
          {loading ? 'Tracking...' : 'Track Product'}
        </button>
      </div>

      {error && (
        <div style={{ color: 'red', marginBottom: 20 }}>
          {error}
        </div>
      )}

      {product && (
        <div style={{ marginTop: 30 }}>
          <h3>{product.name}</h3>
          <img src={product.image} alt={product.name} width="150" style={{ marginBottom: 10 }} />
          <p><strong>Current Price:</strong> â‚¹{product.currentPrice}</p>
          <a href={product.url} target="_blank" rel="noopener noreferrer">View on Amazon</a>

          <div style={{ marginTop: 30 }}>
            <Line data={chartData} />
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
