import React, { useState } from 'react';
import ProductPreview from './ProductPreview';

function App() {
  const [url, setUrl] = useState('');
  const [productId, setProductId] = useState(null);

  const handleTrack = async () => {
    const res = await fetch('http://localhost:5000/api/products/track', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ url }),
    });
    const data = await res.json();
    if (res.ok) {
      setProductId(data.id); // will be used to re-fetch in ProductPreview
    } else {
      alert(data.error);
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <input
        value={url}
        onChange={(e) => setUrl(e.target.value)}
        placeholder="Paste Amazon product URL"
        style={{ width: 300, marginRight: 10 }}
      />
      <button onClick={handleTrack}>Track Product</button>

      {productId && <ProductPreview productId={productId} />}
    </div>
  );
}

export default App;
